package corejaava;
import java.util.*;

public class even {

	public static void main(String[] args) {
		Scanner sc=new Scanner(System.in);
		// TODO Auto-generated method stub
		int n=sc.nextInt();
		int i;
		for(i=0;i<=n;i++)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
		}

	}

}

